(function calculatedFieldValue(current) {
	
	// Add your code here
	if (current.numberperprimaryassignee != '') {
		var gdt = new GlideDateTime();
		var gdtYear = gdt.getYearLocalTime().toString(); // '2018';
		var twoDigitYear = gdtYear.substring(4,2);
		
		return twoDigitYear + "-" + 
			current.numberperprimaryassignee + "-" + 
			current.primary_assignee.getDisplayValue();
	} else return;
	// return the calculated value
	
})(current);

