sp_rectangle_menu_item.record_script:
// Menu Item label

var label = 'Awaiting ARM Promotion';

// maximum number of entries in this Menu
var max = 500;

var t = data;  // shortcut
t.items = [];

var u = gs.getUser().getID();
// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({'table':'x_153465_discovert_war_sar_reporting','filter':'status=submitted'});

var z = new GlideRecord('x_153465_discovert_war_sar_reporting');
//z.addActiveQuery();
z.addQuery('status', 'submitted');
z.addEncodedQuery('assigneeDYNAMIC90d1921e5f510100a9ad2572f2b477fe');
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();


while (z.next()) {

   t.items.push({
    title: z.getValue('task_name') ,
    type: 'link',
    href: '?sys_id='+ z.getValue('sys_id') + '&view=sp&id=discovertasker_taskdetails&table=x_153465_discovert_route_task_instance'
  }); 

  if (!z.canRead())
    continue;

  var a = {};
  //$sp.getRecordValues(a, z, 'short_description,sys_id,number,sys_updated_on');
  //if (z.short_description.nil())
  //  a.short_description = "(No description)";
  a.__table = z.getTableName();
  a.type = 'record';
  //a.sortOrder = z.sys_updated_on.getGlideObject().getNumericValue();
  //t.items.push(a);

}
t.items.sort(function(a, b) {
  return b.sortOrder - a.sortOrder;
});
//t.items = t.items.slice(0, max); // only want first 30
t.items = t.items.slice(0, 5); 
//t.count = t.items.length;
t.count = z.getRowCount();

var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=discovertasker_detailsdrilldown&view=default&table=x_153465_discovert_war_sar_reporting&filter=assignee=' + u + '^statusINsubmitted%5EEQ&label='+label, items: []};
t.items.push(link); // put 'View all requests' first
