sp_rectangle_menu_item.record_script:
// menu item label

var label = 'Awaiting Review';

// maximum number of entries in this Menu
var max = 500;

var t = data;  // shortcut
t.items = [];

var u = gs.getUser().getID();
// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({'table':'x_153465_discovert_route_task_instance','filter':'state=14'});

var z = new GlideRecord('x_153465_discovert_route_task_instance');
//z.addActiveQuery();
//z.addQuery('state', '14');
//z.addEncodedQuery('originatorDYNAMIC90d1921e5f510100a9ad2572f2b477fe');

//z.addEncodedQuery('parent.assigned_toDYNAMIC90d1921e5f510100a9ad2572f2b477fe^active=true^state=14^NQparent.numberISEMPTY^originatorDYNAMIC90d1921e5f510100a9ad2572f2b477fe^active=true^state=14');
z.addEncodedQuery('u_reviewerDYNAMIC90d1921e5f510100a9ad2572f2b477fe^active=true^state=14');

z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();


while (z.next()) {
  t.items.push({
    title: z.getValue('task_name') ,
    type: 'link',
    href: '?sys_id='+ z.getValue('sys_id') + '&view=sp&id=discovertasker_taskdetails&table=x_153465_discovert_route_task_instance'
  });

  if (!z.canRead())
    continue;

  var a = {};
  //$sp.getRecordValues(a, z, 'short_description,sys_id,number,sys_updated_on');
  if (z.short_description.nil())
    a.short_description = "(No description)";
  a.__table = z.getTableName();
  a.type = 'record';
  //a.sortOrder = z.sys_updated_on.getGlideObject().getNumericValue();
  //t.items.push(a);

}
t.items.sort(function(a, b) {
  return b.sortOrder - a.sortOrder;
});
t.items = t.items.slice(0, 5); // only want first 5
//t.items = t.items.slice(0, 1); 
//t.count = t.items.length;
t.count = z.getRowCount();

