//var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=discovertasker_detailsdrilldown&view=Unassigned&table=x_153465_discovert_route_task_instance&filter=originatorDYNAMIC90d1921e5f510100a9ad2572f2b477fe%5EstateIN14%5EEQ', items: []};
//Roles 7 or 5
//var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=discovertasker_detailsdrilldown&view=Unassigned&table=x_153465_discovert_route_task_instance&filter=parent.assigned_toDYNAMIC90d1921e5f510100a9ad2572f2b477fe%5Estate%3D14%5ENQparent.numberISEMPTY%5EoriginatorDYNAMIC90d1921e5f510100a9ad2572f2b477fe%5Estate%3D14%5EEQ', items: []};
var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=discovertasker_detailsdrilldown&view=Unassigned&table=x_153465_discovert_route_task_instance&filter=u_reviewer='+u+'%5Estate%3D14%5EEQ&label='+label, items: []};
