sp_rectangle_menu_item.record_script:
// menu item label

var label = 'Suggested Tasks';
// maximum number of entries in this Menu
var max = 500;

var t = data;  // shortcut
t.items = [];

var u = gs.getUser().getID();
// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({'table':'x_153465_discovert_suggested_taskers','filter':'active=true'});

var z = new GlideRecord('x_153465_discovert_suggested_taskers');
z.addActiveQuery();
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();


while (z.next()) {

   t.items.push({
    title: z.getValue('number') +": " + z.getValue('email_subject') ,
    type: 'link',
    //href: '?id=discovertasker_detailsdrilldown&table=x_153465_discovert_suggested_taskers&filter=active=true&label='+label
    href: '?sys_id='+ z.getValue('sys_id') + '&view=sp&id=discovertasker_newtask&table=x_153465_discovert_suggested_taskers&filter=active=true&label='+label
  }); 

  if (!z.canRead())
    continue;

  var a = {};
  //$sp.getRecordValues(a, z, 'short_description,sys_id,number,sys_updated_on');
  //if (z.short_description.nil())
  //  a.short_description = "(No description)";
  a.__table = z.getTableName();
  a.type = 'record';
  //a.sortOrder = z.sys_updated_on.getGlideObject().getNumericValue();
  //t.items.push(a);

}
t.items.sort(function(a, b) {
  return b.sortOrder - a.sortOrder;
});
//t.items = t.items.slice(0, max); // only want first 30
t.items = t.items.slice(0, 5); 
//t.count = t.items.length;
t.count = z.getRowCount();

//var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=discovertasker_detailsdrilldown&view=Unassigned&table=x_153465_discovert_route_task_instance&filter=reassignment_requested%3Dtrue%5EORstateIN-5%5Esys_created_by%3Djavascript:gs.getUserName();', items: []};
var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=discovertasker_detailsdrilldown&table=x_153465_discovert_suggested_taskers&filter=active=true&label='+label, items: []};
t.items.push(link); // put 'View all requests' first
