sp_widget.client_script:
function ($scope, spUtil) {
//------------------------------------------------------------------------------------------------------------------------
// WIDGET BASE: FIELDS
// COMPANY: DISCOVER TECHNOLOGIES
// CREATED BY: Ayman Alzyoud
//------------------------------------------------------------------------------------------------------------------------
	var c = this;
	c.comments_count = 0;
	spUtil.recordWatch($scope, $scope.data.table, "sys_id=" + $scope.data.sys_id); 
	var onListClick = $scope.$on('$sp.list.click', refreshWidget);
	var onSaveUpdate = $scope.$on('sp.form.record.updated', refreshWidget);
	//This loop determines if there are any comments for this task
	for(var x=0; x < $scope.data.stream.entries.length; x++){
		if($scope.data.stream.entries[x].element == 'comments'){
			c.comments_count++;
		}
	}
//jslog($scope.data.stream.entries)
	function refreshWidget() {
		$scope.server.update();
	}

}

