sp_widget.client_script:
function ($scope, $rootScope,spAriaFocusManager, $http, amb,$timeout, spUtil, $location, $window, nowAttachmentHandler, $log, spAriaUtil, spModal, $uibModal, spNavStateManager) {
	/* widget controller */
	var c = this;

	c.runProcessTemplate = function(){
		c.data.processInstanceName = c.instanceNotation;
		c.data.process_template_objective_user = c.objective;
		c.server.update();
		$('#runTemplateButton').prop( "disabled", true );
		$(".showAlert").hide().show('medium');
		$( "form[name='processInstanceForm']" ).css("display","none");
	};
}

sp_widget.css:
.showAlert{display: none;}
.astrik:before{
   color: red;
   content: "*";
   position: absolute;
   margin-left: -1%;
}

textarea{
  max-width:100%;
}

sp_widget.script:
(function($sp, input, data, options, gs) {
	/* populate the 'data' object */
	var param1;
	data.process_template_sys_id = '';
	data.process_template_objective = '';
	data.processInstanceName = '';
	data.process_template_objective_user = '';

	if(input){
		data.process_template_sys_id =input.process_template_sys_id;
		data.process_template_objective =input.process_template_objective;

		if((input.processInstanceName != '') && (input.processInstanceName != undefined)){			
			if((input.process_template_objective_user != '') && (input.process_template_objective_user != undefined)){
				param1= input.processInstanceName + " npaa$ " + input.process_template_objective_user;
			}else{
				param1 = input.processInstanceName + " npaa$ " + input.process_template_objective;
			}

			data.test = new RunProcessTemplate().runProcessTemplate(data.process_template_sys_id, param1);
		}
	}
})($sp, input, data, options, gs);
