sys_dictionary.calculation:
(function calculatedFieldValue(current) {

 	var daysBeforeSuspenseDateTaskStatusIsWarning = gs.getProperty("x_153465_discovert.#DaysBeforeSuspenseDateTaskStatusIsWarning");

	var now = new GlideDateTime();
	var dateStatus = "";
	var riskStatus ="";
 	// RAJ 2018-03-13 check supsense date before using
 	if(!current.suspense_date.nil()){
 		var suspense = new GlideDateTime(current.suspense_date);
		if(!current.warning_hours.nil()){
			suspense.addDaysLocalTime(-current.warning_hours);
			//suspense.addSeconds(-current.warning_hours*3600);
		}
		else{
		// TODO: move the 3 days into an Application Setting after MVP.
		//       Possible further extension allow each OU to set
		//       Possible further extension allow each User to set
// 			suspense.addSeconds(-259200);// - 3 day
			suspense.addDaysLocalTime(-daysBeforeSuspenseDateTaskStatusIsWarning);// - 3 
		}
 		var new_warningdate = suspense.getValue();
		//TODO Ayman: verifiy 'IF' condition for days
 		if (now > current.suspense_date)
 			dateStatus = "Overdue";
 		else {
 			if (now > new_warningdate) {
 				dateStatus = "Warning";
 			}else{
 				dateStatus = "Projected on time";
 			}
 		}
 	}
 	else {
 		dateStatus = "No Suspense Date";
 	}
	// Pull Risk Information
	if(current.risk_flag==2){
		riskStatus = "High Risk";
	}
	else if (current.risk_flag==1) {
		riskStatus = "Medium Risk";
	}
	else if (current.risk_flag==0) {
		riskStatus = "Low Risk";
	}

	if ((dateStatus=="Overdue")  || (riskStatus=="High Risk")){
		current.status = "Critical";	
	}
	else if ((dateStatus=="Warning")  || (riskStatus=="Medium Risk")){
		current.status = "Warning";	
	}
	else{
		current.status = "Projected on time";
	}

 	return current.status;
})(current);
