sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var UserPermissions = new UserInfoScripts();	
	var tcs = new TaskerCommonScriptingMethods();
	var ouMemberTableName =  "x_153465_discovert_discover_office_members";
	var ouMemberQuery = [{"office_member": current.office_member}];
	var count = 0;
	tcs.queryRelated(ouMemberTableName, ouMemberQuery, function(memberRecord) {
		count++;
	});
	if(count <= 1) {
		// this user has been deleted.
		if(UserPermissions.removeRole(current.office_member, '', 'x_153465_discovert.tasker_user'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_user - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'is_global_tasking_authority', 'x_153465_discovert.tasker_global_Tasking_Authority_member_admin'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_global_Tasking_Authority_member_admin - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'user_is_organization_admin', 'x_153465_discovert.tasker_organizational_admin'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_organizational_admin - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'is_tasker_application_admin', 'x_153465_discovert.tasker_application_admin'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_application_admin - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'can_import_ou_hierarchy', 'x_153465_discovert.tasker_hierarchy_import_user'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_hierarchy_import_user - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'can_manage_ou_members', 'x_153465_discovert.tasker_single_ou_member_admin'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_single_ou_member_admin - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'can_manage_process_permissions', 'x_153465_discovert.tasker_process_template_admin'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_process_template_admin - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'can_manage_global_processes', 'x_153465_discovert.tasker_process_template_global'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_process_template_global - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'has_ou_process_rights', 'x_153465_discovert.tasker_process_template_ou'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_process_template_ou - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'has_personal_process_rights', 'x_153465_discovert.tasker_process_template_user'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_process_template_user - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'user_is_notification_profile_admin', 'x_153465_discovert.tasker_user_profile_admin'))
			gs.addInfoMessage('Role: x_153465_discovert.tasker_user_profile_admin - removed from user: ' + current.office_member.first_name + ' ' + 
	current.office_member.middle_name + ' ' + current.office_member.last_name);

		if(UserPermissions.removeRole(current.office_member, 'can_manage_ou_notification_profile', 'x_153465_discovert.tasker_user_profile_ou_admin'))
