sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	if (!current.parent.nil() && current.isNewRecord())
		{
		// RAJ - currently copying the assignment from the parent down when a new task is added under a parent.
		// RAJ, there is no reason to assign a new child task to the same person.
		// logic removed
		//if (!current.parent.assigned_to.nil()){
		//	current.assigned_to = current.parent.assigned_to;
		//}
		//if (!current.parent.assignment_group.nil()){
		//	current.assignment_group = current.parent.assignment_group;
		//}
		// NOTE - we changed how tasks are assigned and accepted, this logic no longer applies
		//if (!current.parent.primary_assignee.nil()){
		//	var alist2 = ' ';
		//	var officeMembersQuery2 = new GlideRecord('x_153465_discovert_discover_office_members');
		//	// RAJ - STRY0011879 - Dot walking reference field to sys_id
		//	var grTaskParent = new GlideRecord('x_153465_discovert_route_task_instance');
		//	grTaskParent.get(current.getValue("parent"));
		//	officeMembersQuery2.addQuery('discover_office_hierarchy',grTaskParent.getValue("primary_assignee"));
		//	officeMembersQuery2.query();
		//	// we query to get the list of members in the office
		//	while (officeMembersQuery2.next()){
		//		alist2 += (',' + officeMembersQuery2.office_member);
		//	}
		//	current.additional_assignee_list = alist2;
		//}

		// RAJ - added 2017.11.07
		current.instructions = current.parent.instructions;
		current.category = current.parent.category;
		current.warning_hours = current.parent.warning_hours;
		current.classification = current.parent.classification;

		//current.source_definition.duration
		current.assignment_group = current.parent.assignment_group;
		current.primary_assignee = current.parent.primary_assignee;
		current.primary_office = current.parent.primary_office;
		// RAJ - 2018.01.10 added for tasking Authority
		current.tasking_authority = current.parent.tasking_authority;

		//current.source_definition.response
		current.short_description = "Sub Task of " +current.parent.short_description;
		current.sub_category = current.parent.sub_category;
		// RAJ - STRY0010060 - 2018-03-13 Set the default Tasker_Type 
		current.tasker_type = current.parent.tasker_type;
		// RAJ STRY0011656 - this allows us to ONLY move tasks forward that are demo data tasks
		current.demo_data = current.parent.demo_data;

		// ***************** Start of changes	*****************
		// STRY0011008 - RAJ 2018-02-23 move logic to set date on children from after insert to before display.
		//               this allows the user to see what will be set before the save rather than setting things behind the sceens after the fact.
		var gdt = new GlideDateTime();
		var durationAdd;

		// Use parent Estimated Start, if child estimated start not set
		if(!current.parent.expected_start.nil() && current.expected_start.nil()){
			current.expected_start = current.parent.expected_start;
			gdt.setValue(current.expected_start);
		}
		else{
			current.expected_start = gdt.getValue();
		}
		// NOTE: logicall calculating the duration below will overwrite the suspsense date.
		//       If business rules would rather use the parent instead of calculating based on duration, move this below the next If statement
		if(!current.parent.suspense_date.nil()) {
			//current.suspense_date = current.parent.suspense_date;  //KTorres: it is breaking after suspense date validation
			//current.due_date = current.suspense_date; //KTorres: it is breaking after suspense date validation
		}
		// if current record has no duration and no supsense date, and the parent has no suspense date, set it to the parent's duration
		if(!current.parent.calendar_duration.nil()){
			current.calendar_duration = current.parent.calendar_duration;
			durationAdd=current.parent.calendar_duration.dateNumericValue();
			gdt.add(durationAdd);
			current.suspense_date=gdt.getValue();
			current.due_date = current.suspense_date;
		}
		// ***************** End of changes	*****************

		// TODO - don't do this with a single copy, need to determine the right way to do this
		// NOTE: we may want to only copy files from parent tasks if they are flagged somehow.
		// also we may want to copy the response documents from parent (marked only)  Or, the attachments object might be just fine.
		// RAJ - STRY0011879 - Dot walking reference field to sys_id
		GlideSysAttachment.copy('x_153465_discovert_route_task_instance', current.getValue("parent"), 'x_153465_discovert_route_task_instance', current.sys_id);
	}

})(current, previous);
