sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var table = 'x_153465_discovert_route_task_instance';

	try{
		if(current.tasker_type.work_type != "review_item" ) { //Standard Task Type
			if(current.parent && current.parent.assigned_to) {
				current.u_reviewer = current.parent.assigned_to;
				// also set each child's reviewer - needed for assigned member change during update
				var currentChild = getSubTasks(table, current.sys_id);
				while(currentChild.next()) {
					//check if write possible
					if(!currentChild.canWrite()) {
						continue;
					}
					//perform write and update
					currentChild.u_reviewer = current.assigned_to;
					currentChild.update();
				}
		   } else if(current.on_behalf_of) {
				current.u_reviewer = current.on_behalf_of;
		   } else {
			   current.u_reviewer = current.originator;
		   }
		   current.setWorkflow(false);
			current.update();
		} else {
			current.u_reviewer = current.assigned_to;
			current.setWorkflow(false);
			current.update();
		}
	}catch(e) {
		gs.warn("Exception - " + e.message);
	}



	var getSubTasks = function(tableName, sysid) {
		var children = new GlideRecord(tableName);
			children.addQuery('parent', sysid);
			children.query();
			return children;
	};


})(current, previous);
