sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// if current record is new, and the parent is set, set the OU Level of this item to the parent + 1
	if(current.isNewRecord()){
		if (current.parent != ''){
			current.office_level = current.parent.office_level + 1;
		}
		else{
			current.office_level = 1;
		}
	}

	g_scratchpad.maxSortOrder = 0;
	// set the maximum value to display in the routes dropdown
	var gaGetMaxSortOrder;
	if (!current.parent.nil()){
		gaGetMaxSortOrder = new GlideAggregate('x_153465_discovert_discover_office_hierarchy');
		gaGetMaxSortOrder.addQuery('parent', current.parent);
		gaGetMaxSortOrder.addAggregate('MAX', 'level_order');
		gaGetMaxSortOrder.groupBy('parent');
		gaGetMaxSortOrder.query();
		if(gaGetMaxSortOrder.next()){
			g_scratchpad.maxSortOrder = gaGetMaxSortOrder.getAggregate('MAX', 'level_order');
			if(g_scratchpad.maxSortOrder == ''){
				g_scratchpad.maxSortOrder = 0;
			}
		}
	}
	// save current value to set back in form after reloading option list
	g_scratchpad.currentSortOrderValue = current.level_order;


})(current, previous);
