sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var sysId;
	if (current.operation() == "delete") {
		sysId = previous.sys_id;
	}
	else {
		sysId = current.sys_id;
	}
	var TaskAuthRows = 0;
	// RAJ - STRY0011695 - change to use Glide Aggregate
	var gaTaskAuthRows = new GlideAggregate('x_153465_discovert_tasking_authority');
	gaTaskAuthRows.addQuery('organizational_unit', sysId);
	gaTaskAuthRows.addAggregate('COUNT');
	gaTaskAuthRows.query();
	if(gaTaskAuthRows.next()){
		TaskAuthRows = gaTaskAuthRows.getAggregate('COUNT');
	}

	var TaskAuth = new GlideRecord('x_153465_discovert_tasking_authority');
	TaskAuth.addQuery('organizational_unit', sysId);
	TaskAuth.query();
	if (current.includeintaskingauthority == true && current.operation() != "delete") {
		//Query Tasking Authority for this OU Name.
		if (TaskAuthRows > 0){
			//If OU found, update Abbreviation and Name
			while (TaskAuth.next()){
				TaskAuth.description = current.office_name;
				TaskAuth.abbreviation = current.office_abbreviation;
				TaskAuth.active = true;
				TaskAuth.update();
			}
		}
		else { // no Tasking Authority for this OU, create one
			TaskAuth.initialize();
			TaskAuth.organizational_unit = current.sys_id;
			TaskAuth.description = current.office_name;
			TaskAuth.abbreviation = current.office_abbreviation;
			TaskAuth.active = true;
			TaskAuth.insert();
		}
	}
	else { // do not include in Tasking Authority
		if (TaskAuthRows > 0){
			//If OU found, set the Active flag to false
			while (TaskAuth.next()){
				TaskAuth.active = false;
				TaskAuth.update();
			}
		}

	}
})(current, previous);
