sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var tcs = new TaskerCommonScriptingMethods();
	try {
		var answer = false;
		var processTemplateID = current.tasker_process_definition;
		var processTemplateQuery = [];
		processTemplateQuery.push({"sys_id":processTemplateID});
		processTemplateQuery.push({"active":true});
		var processTemplateTableName = "x_153465_discovert_discover_routing_template";
		var processTemplateTaskTableName = "x_153465_discovert_route_tasks";
		var user = gs.getUser();
		var requiredRoles = [
			"x_153465_discovert.tasker_process_template_admin",
			"x_153465_discovert.tasker_process_template_ou"
		];
		var checkTemplateCreator = [];
		var processTemplateCreatorCheck = false;
		var processOu = "";
		if(processTemplateID) {
			checkTemplateCreator = tcs.queryRelatedShort(processTemplateTableName, processTemplateQuery, function(record) {
				//while we're checking, ensure that we get process ou
				try {
					processOu = record.process_ou;
					if(record.process_owner == user.getID()) {
						return true;
					}
				} catch(v){
					gs.warn(v);
				}

				return null;
			}, true);
		}

		if(!processOu) {
			answer = true;
		} else if(checkTemplateCreator && (checkTemplateCreator.length > 0 || checkTemplateCreator.indexOf("true") != -1)) {
			answer = true;
		} else if(tcs.checkUserForSingleRoleMatch(gs.getUser(), ["x_153465_discovert.tasker_process_template_admin"])) {
			answer = true;
		} else if(tcs.checkUserForSingleRoleMatch(gs.getUser(), ["x_153465_discovert.tasker_process_template_ou"])) {
			var userInfoScripts = new UserInfoScripts();
			var currentUserId = user.getID();
			if(processOu !== "" && userInfoScripts.IsUserInOU(processOu, currentUserId)) {
				answer = true;
			}
		} else {
			answer = false;
		}
		if(!answer) {
			tcs.commonMessage("Process Template Task", "You are not permitted to perform this operation.", gs.addErrorMessage);
			current.active = previous.active;
			current.setWorkflow(false);
			current.update();
		} else {
			current.active = false;
			current.setWorkflow(false);
			current.update();
			var processTemplateTaskQuery = [];
			var markInactive = function(record) {
				record.active = false;
				record.update();
				return true;
			};
			processTemplateTaskQuery.push({"parent":current.sys_id});
			var numberOfSet = tcs.queryRelated(processTemplateTaskTableName, processTemplateTaskQuery, markInactive);
		}
	} catch(e) {
		tcs.checkException("ProcessTemplateTask", gs.warn, e);
	}
})(current, previous);
