sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// RAJ for now, don't set members
	var alist = ' ';
	var officeMembersQuery = new GlideRecord('x_153465_discovert_discover_office_members');
	if (!current.original_tasker_inst.nil() ) {
		var grTaskInst = new GlideRecord('x_153465_discovert_route_task_instance');
		grTaskInst.get(current.getValue("original_tasker_inst"));

		// if the task was assigned to a user we may want to come back here and set it to a user.
		if (!grTaskInst.assigned_to.nil()){
			current.assigned_to_type = 'user';
			current.assigned_to_individual = current.assigned_to;
		} else if (!grTaskInst.assignment_group.nil()){
			current.assigned_to_group = grTaskInst.assignment_group;
			current.assigned_to_type = 'group';
		} else {
			current.assigned_to_type = 'office';
		}

		current.instructions = grTaskInst.instructions;
		current.category = grTaskInst.category;
		current.warning_hours = grTaskInst.warning_hours;
		current.classification = grTaskInst.classification;
		current.primary_assignee = grTaskInst.primary_assignee;
		current.primary_office = grTaskInst.primary_office;
		current.route_order = grTaskInst.order;
		// TODO - don't do this with a single copy, need to determine the right way to do this
		//grTaskInst.response
		current.short_description = grTaskInst.short_description;
		current.sub_category = grTaskInst.sub_category;
		// RAJ - Duration was not copied down and should have been
gs.info('DiscoverTasker Business Rule - TaskerDefInsertCopyInstance - Duration set from calendar_duration: ' + grTaskInst.calendar_duration);
		current.duration = grTaskInst.calendar_duration;

		current.tasker_type = grTaskInst.tasker_type;
		current.tasking_authority = grTaskInst.tasking_authority;
		current.demo_data = grTaskInst.demo_data;

		// NOTE: we may want to also copy the response documents from source to destination.  Or, the attachments object might be just fine.
		GlideSysAttachment.copy('x_153465_discovert_route_task_instance', current.getValue("original_tasker_inst"), 'x_153465_discovert_route_tasks', current.sys_id);
	}


})(current, previous);
