sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var tcs = new TaskerCommonScriptingMethods();
	try {
		var user = gs.getUser();
		var createdBy = current.getValue("sys_created_by");
		var processOwner = current.getValue("process_owner");
		var roles = ["x_153465_discovert.tasker_process_template_admin"];
		var scheduleTableName = "x_153465_discovert_tasker_schedule";
		var taskTableName = "x_153465_discovert_route_task_instance";
		var processTemplateTaskTableName = "x_153465_discovert_route_tasks";
		var sysId = current.sys_id;
		var hasActive = false;
		var hasTaskActive = false;
		var hasScheduleActive = false;
		var processScheduleQuery = [];
		processScheduleQuery.push({"tasker_process":sysId});
		var taskQuery = [];
		var isActive = function(record) {

		};
		taskQuery.push({"child_route_template":sysId});
		tcs.queryRelatedShort(scheduleTableName, processScheduleQuery, function(record) {
				hasActive = true;
				hasScheduleActive = true;
				return null;
		}, true);
		tcs.queryRelatedShort(taskTableName, taskQuery, function(record) {
			if(record.getValue("active") == true || record.getValue("u_active") == true) {
				hasActive = true;
				hasTaskActive = true;
				return null;
			} else return false;
		}, true);


		if(hasActive || !(user.getID() == processOwner || tcs.checkUserForSingleRoleMatch(user, roles))) {
			var message = "";
			if(hasScheduleActive) {
				message = "Schedules";
			}
			if(hasTaskActive) {
				if(message != "") {
					message += " and Tasks";
				} else {
					message += "Tasks";
				}
			}
			if(hasActive && message != "") {
				tcs.commonMessage("Process Template", message + " are still active. Please deactivate them and try again.", gs.addErrorMessage);
			} else {
				tcs.commonMessage("Process Template", "You are not permitted to perform this operation.", gs.addErrorMessage);
			}
			current.u_active = true;
			current.setWorkflow(false);
			current.update();
		} else {
			current.u_active = false;
			current.update();
			var processTemplateTaskQuery = [];
			processTemplateTaskQuery.push({"sys_id":current.parent_task_def});
			var markInactive = function(record) {
				record.active = false;
				current.setWorkflow(false);
				record.update();
				return true;
			};
			tcs.queryRelated(processTemplateTaskTableName, processTemplateTaskQuery, markInactive);
		}
	} catch(e) {
		tcs.checkException("ProcessTemplate - Mark Inactive",gs.warn, e);
	}
})(current, previous);
