sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	// RAJ - STRY 0011909 - modify process template
	// Create Top Tasker Def
	// Tasker Definition Record
	var recTaskDef = new GlideRecord('x_153465_discovert_route_tasks');
	recTaskDef.initialize();
	// set the process definition for the Tasker Def
	recTaskDef.tasker_process_definition = current.sys_id;
	recTaskDef.short_description = current.short_description;
	recTaskDef.instructions = current.description;
	recTaskDef.u_objective = current.u_objective;

	recTaskDef.route_order = 1;
	// Default the Process Definition Fields on creation here
	// Lookup the default ou for this user and set the Primary OU
	var OrgScripts = new OrganizationalScripts();
	var defaultOU = OrgScripts.GetUserDefaultOU(gs.getUserID());
	if((defaultOU.toString() != '') || (defaultOU.toString()  != undefined )){
		recTaskDef.primary_office = defaultOU;
		recTaskDef.primary_assignee = defaultOU;
		gs.info('primary_office: ' + defaultOU)
		// RAJ 2018-02-20 - STRY0010804 - default Tasking Authority to the current user's OU
		var defaultTA;
		defaultTA = OrgScripts.GetUserDefaultTaskingAuthority(defaultOU);

		if((defaultTA.toString() != '') || (defaultTA.toString()  != undefined )){
			recTaskDef.tasking_authority = defaultTA;
		}

	}
	recTaskDef.insert();

//	gs.info("Tasker Process Defintion Insert - Top Task Definition Created sys_id:" + recTaskDef.sys_id);

	// Set the Top Task on the Process Instance
	current.parent_task_def = recTaskDef.sys_id;
	current.update();


})(current, previous);
