sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	// Check to see if the current Task Def has children
	g_scratchpad.calchaschildren = 0;
	g_scratchpad.taskDefParent = current.getValue('parent');
	var gaGetChildCount = new GlideAggregate('x_153465_discovert_route_tasks');
	gaGetChildCount.addQuery('parent', current.getValue('parent'));
	gaGetChildCount.addAggregate('COUNT');
	gaGetChildCount.query();
	if(gaGetChildCount.next()){
		g_scratchpad.calchaschildren = gaGetChildCount.getAggregate('COUNT');
	}

	g_scratchpad.maxRoutes = 0;
	// set the maximum value to display in the routes dropdown
	var gaGetMaxRoute;
	if (!current.parent.nil()){
//gs.addInfoMessage('Calculating Max routes for task with Parent: ' + current.parent.short_description);
		gaGetMaxRoute = new GlideAggregate('x_153465_discovert_route_tasks');
		gaGetMaxRoute.addQuery('parent', current.getValue('parent'));
		gaGetMaxRoute.addAggregate('MAX', 'route_order');
		gaGetMaxRoute.groupBy('parent');
		gaGetMaxRoute.query();
		if(gaGetMaxRoute.next()){
			g_scratchpad.maxRoutes = gaGetMaxRoute.getAggregate('MAX', 'route_order');
//gs.addInfoMessage('scratchpad set to max routes');
		}
	}
	// NOTE: TODO - THIS GOES AWAY WHEN WE ADD THE TOP TASKER DEF
	else if(!current.tasker_process_definition.nil()){
//gs.addInfoMessage('Calculating Max routes for task with Parent: ' + current.tasker_process_definition.template_name);
		gaGetMaxRoute = new GlideAggregate('x_153465_discovert_route_tasks');
		gaGetMaxRoute.addQuery('tasker_process_definition', current.getValue('tasker_process_definition'));
		gaGetMaxRoute.addAggregate('MAX', 'route_order');
		gaGetMaxRoute.groupBy('tasker_process_definition');
		gaGetMaxRoute.query();
		if(gaGetMaxRoute.next()){
			g_scratchpad.maxRoutes = gaGetMaxRoute.getAggregate('MAX', 'route_order');
//gs.addInfoMessage('scratchpad set to max routes');
		}
	}
	// save current value to set back in form after reloading option list
	g_scratchpad.currentRouteValue = current.route_order;

//gs.addInfoMessage('Calculated max Route to be: ' + g_scratchpad.maxRoutes);
})(current, previous);
