sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue == oldValue) {
		return;
	}

	if(g_form.getValue('tasker_process') != ''){
		// RAJ 2018-07-19 - STRY0012696 - added this to remove the getReference calls
		//g_form.getReference('tasker_process', setNameField);
		var ga = new GlideAjax('TaskerSchedulerScript');
		ga.addParam('sysparm_name', 'ajaxFunction_getProcessName');
		ga.addParam('sysparm_Tasker_Process', g_form.getValue('tasker_process'));
		ga.getXML(setNameField);
	}
	else{
		g_form.setValue('name', ' - ' + g_form.getValue('repeat_description'));
	}
}
function setNameField(response){
	// RAJ 2018-07-19 - STRY0012696 - added this to remove the getReference calls
	//g_form.setValue('name', process.template_name + ' - ' + g_form.getValue('repeat_description'));
	var answer = response.responseXML.documentElement.getAttribute("answer");
	if (answer != ''){
		g_form.setValue('name', answer + ' - ' + g_form.getValue('repeat_description'));
	}
	else{
		g_form.setValue('name', ' - ' + g_form.getValue('repeat_description'));
	}
}
