sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	var suspense_date = g_form.getValue('suspense_date');
	var suspense_date_split = suspense_date.split(" ");
	var modified_suspense_date = suspense_date_split[0].split(",");

	var ajax = new GlideAjax('MyDateTimeAjax'); 


	ajax.addParam('suspense_date',suspense_date);
	ajax.addParam('sysparm_name', 'nowDateTime'); 

	ajax.getXML(function () 
	{ 

		var data = ajax.getAnswer();
		var newDate = (JSON.parse(data));
		g_form.setValue('suspense_date', modified_suspense_date + ' 23:59:59');
		current_dateTime = newDate.now;

 		if((current_dateTime > suspense_date) && (g_form.isReadOnly('suspense_date') != true) ){
			g_form.showErrorBox('suspense_date','The Suspense date must be in the future.',true);
		//	alert("The Suspense date must be in the future.");
		}

	}); 

}
