sys_script_client.script:
function onLoad() {
	var originator = g_form.getValue('originator');
	var reviewer = g_form.getValue('u_reviewer');
	var assignedTo = g_form.getValue('assigned_to');
	var globalTa =  g_form.getDisplayValue('tasking_authority');
	var currentUser = g_user.userID;
	var state = g_form.getValue('state');

	if((state != (3 , 4 , 7)) && (currentUser == reviewer)){
		g_form.setReadOnly('suspense_date',false);
		g_form.setReadOnly('risk_flag',false);
	}

	var clientOrgScripts = new GlideAjax('x_153465_discovert.ClientOrganizationalScripts');
	clientOrgScripts.addParam('sysparm_name', 'GetUserDefaultOU');
	clientOrgScripts.addParam('UserId', currentUser);
	clientOrgScripts.getXMLAnswer(function(answer) {
		var clientUserInfoScripts = new GlideAjax('x_153465_discovert.ClientUserInfoScripts');
		clientUserInfoScripts.addParam('sysparm_name', 'IsUserTaskingAuthority');
		clientUserInfoScripts.addParam('UserId', currentUser);
		clientUserInfoScripts.addParam('OU', answer);
		clientUserInfoScripts.getXMLAnswer(function(isUserTA) {
			if(isUserTA == 'true' && state != (3 , 4 , 7)) {
				g_form.setReadOnly('suspense_date',false);
				g_form.setReadOnly('risk_flag',false);
			}
		});
	});
}
