function onLoad() {
	//var today = Date();
	//var today = gdt.Date();

	//g_form.setValue('start_date',today);

	setDayOfWeek();
	setRepeatText();
}

// NOTE: RAJ - this should go into a script that loads for all screens one time.
// any changes made here should also be made in all onChange events and all onLoad events for the Tasker Scheduler form
// 2 functions, setRepeatText() and setRepeatTextResponse(response) have been added to each script
function setRepeatText() {	
	var repeatCount = g_form.getValue("repeat_count");
	var startDate = g_form.getValue("start_date").split(' ')[0]; //Gets the Date;

	if (!startDate) {
		startDate = "";
	}
	if (!repeatCount) {
		repeatCount = 0;
	}

 	var ga = new GlideAjax("TaskerSchedulerScript");
 	ga.addParam('sysparm_name', 'scheduleTime');
 	ga.addParam('sysparm_repeat_type', g_form.getValue("repeat_type"));
 	ga.addParam('sysparm_repeat_count', repeatCount);
 	ga.addParam('sysparm_start_date', startDate);
 	ga.addParam('sysparm_days_of_week', g_form.getValue("days_of_week"));
 	ga.addParam('sysparm_monthly_type', g_form.getValue("monthly_type"));
 	ga.addParam('sysparm_yearly_type', g_form.getValue("yearly_type"));
  	ga.getXML(setRepeatTextResponse, null);
}

function setRepeatTextResponse(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue("repeat_description",answer);
}

function setDayOfWeek() {
	var repeatType = g_form.getValue("repeat_type");
	var startDate = g_form.getValue("start_date");
	if (repeatType == 'weekly' ){		
		var ga = new GlideAjax("TaskerSchedulerScript");
		ga.addParam('sysparm_name', 'getDayOfWeek');
		ga.addParam('sysparm_start_date', startDate);
		ga.getXML(setRepatOnField, null);
	}
}

function setRepatOnField(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue("days_of_week",answer);
}

