sys_script_client.script:
function onLoad() {
	// RAJ STSK0011105 2018-03-20 restrict changing tasking authority
	var bIsAdmin = g_user.hasRoleExactly('admin');
	var bIsGlobalTaskingAuthority  = g_user.hasRoleExactly('x_153465_discovert.tasker_global_Tasking_Authority_member_admin');
	var bIsTaskerApplicationAdmin = g_user.hasRoleExactly('x_153465_discovert.tasker_application_admin');
	var bIsTaskerOrganizationalAdmin = g_user.hasRoleExactly('x_153465_discovert.tasker_organizational_admin');
	var bIsTaskerProcessTemplateAdmin = g_user.hasRoleExactly('x_153465_discovert.tasker_process_template_admin');
	var bIsTaskerUserProfileAdmin = g_user.hasRoleExactly('x_153465_discovert.tasker_user_profile_admin');

	g_scratchpad.isOuAdmin = false;

	g_form.setDisabled('is_global_tasking_authority', !(bIsAdmin || bIsGlobalTaskingAuthority));
	g_form.setDisabled('is_tasker_application_admin', !(bIsAdmin || bIsTaskerApplicationAdmin));
	g_form.setDisabled('user_is_organization_admin', !(bIsAdmin || bIsTaskerApplicationAdmin));

	// NOTE: g_scratchpad.isOuAdmin is currently based ONLY on the OU being worked.
	//       a future update should apply parent and ancestor logic if approved 
	g_form.setDisabled('office_task_authority', !(bIsGlobalTaskingAuthority || g_scratchpad.isOuAdmin));

	// NOTE: g_scratchpad.isOuAdmin is currently based ONLY on the OU being worked.
	//       a future update should apply parent and ancestor logic if approved 
	g_form.setDisabled('is_office_administrator', !(bIsTaskerOrganizationalAdmin || g_scratchpad.isOuAdmin));
	g_form.setDisabled('can_import_ou_hierarchy', !(bIsTaskerOrganizationalAdmin));
	g_form.setDisabled('can_manage_ou_members', !(bIsTaskerOrganizationalAdmin));

	// Process Admin Rights
	g_form.setDisabled('can_manage_process_permissions', !bIsTaskerOrganizationalAdmin);
	g_form.setDisabled('can_manage_global_processes', !bIsTaskerProcessTemplateAdmin);
	g_form.setDisabled('has_ou_process_rights', !(bIsTaskerProcessTemplateAdmin || g_scratchpad.isOuAdmin));
	g_form.setDisabled('has_personal_process_rights', !(bIsTaskerProcessTemplateAdmin || g_scratchpad.isOuAdmin));

	//INACTIVE
	// Notification Rights
	/*
	g_form.setDisabled('user_is_notification_profile_admin', !bIsTaskerOrganizationalAdmin);
	g_form.setDisabled('can_manage_ou_notification_profile', !(bIsTaskerUserProfileAdmin || g_scratchpad.isOuAdmin));	
	g_form.setDisabled('can_manage_personal_notification_profile', !(bIsTaskerUserProfileAdmin || g_scratchpad.isOuAdmin));
	*/

}
