sys_script_client.script:
function onLoad() {
	// the route order field needs to only allow selection of valid route orders.
	// this script is connected to the Tasker Def - onLoad - Set Route Choices to repopulate teh option list for the route order drop down
	// NOTE: since the options don't always exist, it appears that the display values are not present with all options above 1.
	// this could be fixed by adding 50 - 100 values just to make it look good. 
	// or, something might have to be changed in the list view to show the value instead of the display value 
//alert('g_scratchpad.currentTaskOrderValue: ' + g_scratchpad.currentTaskOrderValue);
	g_form.clearOptions('task_order');
	var i = 0;
	while (i <= g_scratchpad.maxTaskOrder){
		i++;
		g_form.addOption('task_order', i, i);
	}
	// set the route order back after reloading the dropdown list
	g_form.setValue('task_order',g_scratchpad.currentTaskOrderValue,g_scratchpad.currentTaskOrderValue);

}
