sys_script_client.script:
function onLoad() {
	// the sort order field needs to only allow selection of valid sort orders.
	// this script is connected to the Tasker OU - Tasker OU - onDisplay - set OU Level
	// Choices are repopulated in the option list for the sort order drop down based on the max sort order for the parent
	// NOTE: since the options don't always exist, it appears that the display values are not present with all options above 1.
	// this could be fixed by adding 50 - 100 values just to make it look good. 
	// or, something might have to be changed in the list view to show the value instead of the display value 
	g_form.clearOptions('level_order');
	var i = 0;
	while (i <= g_scratchpad.maxSortOrder){
		i++;
		g_form.addOption('level_order', i, i);
	}
	// set the sort order back after reloading the dropdown list
	g_form.setValue('level_order',g_scratchpad.currentSortOrderValue,g_scratchpad.currentSortOrderValue);

}
