sys_script_client.script:
function onSubmit() {

	var subTasks = g_form.getValue('calchaschildren');
	var taskNumber = g_form.getValue('number');
	var cancelation_confirmation;
	var currentUser = g_user.getFullName();
	var cancelation_reason= '';

	function getCancelationReason() {

		cancelation_reason = prompt("Please provide a reason to why you want to cancel this task: ");
		try {
			if(cancelation_reason == null)
				return false;
			if (cancelation_reason == '' || cancelation_reason == undefined) {

				throw err;
			} else {
				g_form.setValue('u_cancellation_reason', cancelation_reason);
				g_form.setValue('u_canceled_by',currentUser);

			}
		} catch (err){
			alert("You must provide a cancelation reason");
			getCancelationReason();
		}
	}

	if (g_form.getActionName() == 'cancel') {

		if (subTasks > 0) {
			cancelation_confirmation = confirm("Are you sure you want to cancel this task? " + taskNumber + " has " + subTasks + " sub-tasks that are also going to be canceled. Do you want to continue?");
		} else {
			cancelation_confirmation = confirm("Are you sure you want to cancel this task?");
		}

		if (cancelation_confirmation == true) {

			getCancelationReason();
			if(cancelation_reason == null)
				return false;
		}else {
			return false;
		}
	}

}
