sys_script_client.script:
function onSubmit() {
	var taskerTable = 'x_153465_discovert_route_task_instance';
	if (g_form.getActionName() == 'Create Suggested Tasker') {
		getNewTaskId();
	}
	function getNewTaskId(){
		var ga = new GlideAjax("ClientTaskInfo");
		ga.addParam('sysparm_name', 'taskGetTaskSysId');
		ga.addParam('suggested_task_sys_id', g_form.getUniqueValue());
		setTimeout(function(){
			ga.getXML(getNewTaskID, null);
		}, 200);

		function getNewTaskID(response) {
			var answer = response.responseXML.documentElement.getAttribute("answer");
			location.href="../discovertaskerportal?id=discovertasker_taskdetails&table=" + taskerTable + "&sys_id="+ answer+ "&spa=1 target=_top";
		}
	}
}
