sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue == '') {
		return;
	}
	var extended_suspense_date = g_form.getValue('extended_suspense_date');
	var extended_suspense_date_split = extended_suspense_date.split(" ");
	var modified_extended_suspense_date = extended_suspense_date_split[0].split(",");

	//if(g_form.getValue('suspense_date') == newValue || g_form.getValue('suspense_date') < newValue)
	if(g_form.getValue('extended_suspense_date') <= g_form.getValue('suspense_date'))
		{
			alert("The Extended Suspense Date must be after the Suspense Date.");
			g_form.clearValue('extended_suspense_date');
			return;
		}
	g_form.setValue('extended_suspense_date', modified_extended_suspense_date + ' 23:59:59');

	if(g_form.getValue('calchaschildren') > 0)
		{
		var duration = g_form.getValue('calendar_duration');
		var extended_change = getDifferenceInDays(g_form.getValue('suspense_date').substring(0,10), newValue.substring(0,10));

		if (duration > 0)
			{
			var percent = ((extended_change * 24)/duration)/100;
			g_form.setValue('suspense_change_type', 'percent');
			g_form.setValue('suspense_change', percent);
		}
		else
			{
			g_form.setValue('suspense_change', extended_change);
			g_form.setValue('suspense_change_type', 'days');
		}

		alert("Extending the suspense date will effect all children tasks.");
	}
}

function getDifferenceInDays(dt1, dt2)
{
	var date1 = new Date(dt1);
	var date2 = new Date(dt2);
	var dateDiff = new Date();

	if (date2 > date1)
		{
		return getDifferenceInDays(date2, date1);
	}
	else if (date2 < date1)
		{
		dateDiff = Math.floor((date1 - date2) / (1000 * 60 * 60 * 24));    // get difference in days
	}
	return dateDiff;
}
