function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
 var blnChangeAssignment = true;
 // RAJ 2018-02-13 if there was a Tasking Authority assigned, check if the task has children.
 if (oldValue != ""){
	// TODO, WHEN HERE FOR DIALOG FIX THIS.
	// THERE IS NO CHILDREN COUNT ON A TASK DEFINITION.
	// EITHER ADD A GLIDE AGGREGATE OR ADD LOGIC AND FIX SCRIPT TO KEEP TRACK OF CHILDREN SO THIS WORKS
 	if (g_scratchpad.calchaschildren>0){
// TODO - THIS MESSAGE NEEDS TO BE CHANGED INTO A DIALOG				
// 		blnChangeAssignment = confirm('THIS MESSAGE NEEDS TO BE CHANGED INTO A DIALOG SO USERS CAN MAKE A CHOICE AND THE TEXT IS READ FROM A LANGUAGE TABLE...                                 Changing the Tasking Authority will force an update on the Tasking Authority on all descendents of this Process Definition.  Do you still want to change the Tasking Authority?');
 	}
// TODO - would like to set the field on teh screen back to the old value.
			// when the line below is added, the screen fires the onchange again.
			// need to figure out how to change back without the popup coming up again.
			//g_form.setValue('tasking_authority',oldValue);	 
 	if(!blnChangeAssignment){
 		return false;  // reset the Tasking Authority back to the old value
 	}
 }
	// RAJ - remove getReference
	//g_form.getReference('tasking_authority', useTaskingAuthorityReference);
	if (newValue) {
		if (newValue != oldValue) {
			var ga = new GlideAjax('ClientOrganizationalScripts');
			ga.addParam('sysparm_name', 'getTaskingAuthorityOrganizationalUnit');
			ga.addParam('sysParam_TaskingAuthority', g_form.getValue('tasking_authority'));
			ga.getXML(updatePrimaryOffice);
		}
	}

}

function updatePrimaryOffice(response) {

	var answer = response.responseXML.documentElement.getAttribute("answer");
	if (answer != ''){
		g_form.clearValue('primary_office');
		g_form.setValue('primary_office',answer);
		g_form.setReadOnly('primary_office',true);
	}
	else{
		g_form.setReadOnly('primary_office',false);
	}

}
