sys_script_client.script:
function onLoad() {

	// Type appropriate comment here, and begin script below
	// Determine duration.
	// RAJ STSK0011104 - allow changing task assingment based on user being a tasking authority 
	// changed the script include to add a method to determine if the user is a Tasking Authority
 	var UserPermissions  = new GlideAjax('ClientUserInfoScripts');
 	UserPermissions.addParam('sysparm_name','CanUserAssignSelfOrOthers');
 	UserPermissions.addParam("OU", g_form.getValue('primary_assignee'));
 	UserPermissions.addParam("UserId", g_user.userID);

 	// call script include to determine if this user is a tasking authority of the primary assignee OU.
 	UserPermissions.getXML(f_returnCanUserAssignSelfOrOthers);
}


function f_returnCanUserAssignSelfOrOthers(response){
	if (g_form.getValue('active') == 'true' ){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		// if the current user is a Tasking Authority in the OU, allow them to change the assigned members
		g_form.setDisplay('assigned_to',answer);
		g_form.setDisabled('assigned_to', !answer);
		g_form.setDisplay('additional_assignee_list',answer);
		g_form.setDisabled('additional_assignee_list', !answer);
	}
}
