	if (isLoading || newValue === '') {
		return;
	}

	var blnChangeAssignment = true;
	// RAJ 2018-02-13 if there was a Tasking Authority assigned, check if the task has children.
	if (oldValue != ""){
		if (g_form.getValue('calchaschildren')>0){
// TODO CHANGE TO DIALOG
//			blnChangeAssignment = confirm('THIS MESSAGE NEEDS TO BE CHANGED INTO A DIALOG SO USERS CAN MAKE A CHOICE AND THE TEXT IS READ FROM A LANGUAGE TABLE...                                 Changing the Primary Office will force an update on the Primary Office  on all descendents of this Tasker.  Do you still want to change the Primary office?');
		}
		if(!blnChangeAssignment){
// TODO - would like to set the field on teh screen back to the old value.
			// when the line below is added, the screen fires the onchange again.
			// need to figure out how to change back without the popup coming up again.
			//g_form.setValue('primary_office',oldValue);
			return false;  // reset the Tasking Authority back to the old value
		}
	}

}
