sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	if (current.extended_suspense_date && current.calchaschildren > 0)
		{
		var child = new GlideRecord('x_153465_discovert_route_task_instance');
		var parent = new GlideRecord('x_153465_discovert_route_task_instance');
		var parentID = current.sys_id;
		gs.info("Tasker Extended Suspense Date Update - Task: " + current.number + " Initiatied By: " + current.sys_updated_by);

		//Query children tables for all records with current parent.
		child.addQuery('parent', current.sys_id);
		child.query();

		while (child.next())
			{

			//Setup GlideDateTime
			var gDate = new GlideDateTime(child.extended_suspense_date);
			var dateasint = gDate.getNumericValue();

			//Determine the ype of change.
			if (current.suspense_change_type == "percent")
				{
				var changeInPercent = parseFloat(current.suspense_change);
				gDate.addSeconds(changeInPercent * (gDate.getNumericValue()/1000));
				child.extended_suspense_date = gDate.getDate();
			}
			else if (current.suspense_change_type == "days")
				{
				if (current.extended_suspense_date < current.suspense_date)
					{
					gDate.addDaysUTC(current.suspense_change * -1);
					child.extended_suspense_date = gDate.getDate();
				}
				else if (current.extended_suspense_date > current.suspense_date)
					{
					gDate.addDaysUTC(current.suspense_change);
					child.extended_suspense_date = gDate.getDate();
				}
			}

			child.update();
			//Load next child.
			child.next();
		}
		current.update();
	}


})(current, previous);
