sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	g_scratchpad.maxTaskOrder = 0;
	g_scratchpad.taskParent = current.parent;
	// set the maximum value to display in the routes dropdown
	var gaGetMaxTaskOrder;
	if (!current.parent.nil()){
//gs.addInfoMessage('Calculating Max routes for task with Parent: ' + current.parent.short_description);
		gaGetMaxTaskOrder = new GlideAggregate('x_153465_discovert_route_task_instance');
		gaGetMaxTaskOrder.addQuery('parent', current.parent);
		gaGetMaxTaskOrder.addAggregate('MAX', 'task_order');
		gaGetMaxTaskOrder.groupBy('parent');
		gaGetMaxTaskOrder.query();
		if(gaGetMaxTaskOrder.next()){
			g_scratchpad.maxTaskOrder = gaGetMaxTaskOrder.getAggregate('MAX', 'task_order');

//gs.addInfoMessage('scratchpad set to max routes g_scratchpad.maxTaskOrder: ' + g_scratchpad.maxTaskOrder);
		}
	}
	// save current value to set back in form after reloading option list
	g_scratchpad.currentTaskOrderValue = current.task_order;

//gs.addInfoMessage('Calculated max Route to be: ' + g_scratchpad.maxTaskOrder);
})(current, previous);
