sys_script_fix.script:
(function execute() {
	gs.info("Executing 'Sync of Org Hierarchy with Org Unit Tables'");
	var orgHierarchyTableName = "x_153465_discovert_discover_office_hierarchy";
	var orgUnitTableName = "x_153465_discovert_discover_office_members";

	try{
		//get hierarchy table
		var hierarchyTable = new GlideRecord(orgHierarchyTableName);
		hierarchyTable.query();
		//function to get org unit table with office auth based on current OU
		var getOuUnitTable = function(currentOUHierarchySysId) {
			var ouUnitTable = new GlideRecord(orgUnitTableName);
			ouUnitTable.addQuery("discover_office_hierarchy", currentOUHierarchySysId);
			ouUnitTable.addQuery("office_task_authority", "true");
			ouUnitTable.query();
			return ouUnitTable;
		};
		//function to get org unit table with global auth based on current OU
		var getGlobalUnitTable = function(currentGlobalHierarchySysId) {
			var globalUnitTable = new GlideRecord(orgUnitTableName);
			globalUnitTable.addQuery("discover_office_hierarchy", currentGlobalHierarchySysId);
			globalUnitTable.addQuery("is_global_tasking_authority", "true");
			globalUnitTable.query();
			return globalUnitTable;
		};
		//for each record in hierarchy table
		var globalHierarchyList = "";
		while(hierarchyTable.next()) {
			gs.info("Fix Script - Sync Org Hierarchy - Checking OU: ====" + hierarchyTable.getValue("office_name") + "====");
			try {
				// get ou sysid
				var ouId = hierarchyTable.getValue("sys_id");
				// find all records in unit table with matching OU
				var unitTableOU = getOuUnitTable(ouId);
				// for each of the records in unit table
				var hierarchyList = "";
				while(unitTableOU.next()) {
					try {
						//add user ref of any record containing true for 
						//"office_task_authority" to hierarchy table list "tasking_authorities"		
						var otaUserId = unitTableOU.getValue("office_member");
						hierarchyList  = (hierarchyList) ? hierarchyList + "," + otaUserId : otaUserId;
					} catch(ex_uTableOU) {
						gs.warn(ex_uTableOU);
					}

				}
				hierarchyTable.setValue("tasking_authorities", hierarchyList);
				gs.info("Fix Script - Sync Org Hierarchy - OU_TA Users: " + hierarchyList);
				// then do the same for global tasking authorities
				var unitTableGlobal = getGlobalUnitTable(ouId);

				while(unitTableGlobal.next()) {
					try {
						var globalMember = unitTableGlobal.getValue("office_member");
						if(globalHierarchyList.indexOf(globalMember) == -1) {
							globalHierarchyList = (globalHierarchyList) ? globalHierarchyList + "," + globalMember : globalMember;
						}
					} catch(ex_uTableGl) {
						gs.warn(ex_uTableGl);
					}
				}

				gs.info("Fix Script - Sync Org Hierarchy - collected Global TA Users: " + globalHierarchyList);
				// if anything changed, call the update (max once per record)
				hierarchyTable.update();
			} catch(ex_htable) {
				gs.warn(ex_htable);
			}
		}
		hierarchyTable = new GlideRecord(orgHierarchyTableName);
		hierarchyTable.query();
		gs.info("Fix Script - Sync Org Hierarchy - Setting ALL Global TA");
		while(hierarchyTable.next()) {
			gs.info("Fix Script - Sync Org Hierarchy - Setting Global TA for " + hierarchyTable.getValue("office_name"));
			hierarchyTable.setValue("u_global_authorities", globalHierarchyList);
			hierarchyTable.update();
		}
		gs.info("Fix Script - Sync Org Hierarchy - COMPLETE");
	} catch(e) {
		gs.warn(e);
	}
})();
