sys_script_fix.script:
/*jshint esnext: true */
var tcs  = new TaskerCommonScriptingMethods();
var userTableName = "sys_user";
var ouMemberTableName = "x_153465_discovert_discover_office_members";

//synchronizes all checked fields with the found member records
var allUsers = tcs.queryRelated(userTableName, null, function(user) {
	var ouMemberQuery = [{"office_member": user.sys_id}];
	var modCount = 0;
	tcs.queryRelated(ouMemberTableName, ouMemberQuery, function(memberRecord) {
		memberRecord.sys_mod_count++;
		memberRecord.update();
		modCount++;
		return null;
	});
	if(modCount > 0) {
		tcs.commonMessage("OU Perm Fix", "User: " + user.name + " "+ modCount+ " record(s) updated.", gs.info );
	}
	modCount = 0;
});

