TaskerSuspenseDateProcessing.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// create a function to process all child
	// pass in Current Object from client which is tasker object, template sys_id = parent; previous Suspense Date = previousSD; current Suspsense Date = currentSD; precent is boolean; date is boolean.
	updatechild: function()
	{
		var child = new GlideRecord('x_153465_discovert_route_task_instance');
		var type = this.getParameter('type');
		var change = this.getParameter('change');
		//Query child tables for all records with current parent.
		child.addQuery('parent', this.getParameter('parent'));
		chil.query();

		//If the aggrigate of the query is greater than 0 proceed.

		while (child.next())
			{
			if (type == 'percent')
				{
				//percent calculation
			}
			else if (type == 'days')
				{
				child.suspense_date.addDays(change);
			}
			//Get the next child in the query.
			child.next();
		}

	},

	// NOTE: we may have to add a similiar function for Template Definition and Route Definition on Duration change to push change down the tree.
	type: 'TaskerSuspenseDateProcessing'
