sys_script_include.script:
var Tasker_RecurringTask_Functions = Class.create();
Tasker_RecurringTask_Functions.prototype = {
	initialize: function() {
	},

	findAndStartRecurringTaskersDaily: function(){
		var grSchedules = new GlideRecord('x_153465_discovert_tasker_schedule');
		var gqc = grSchedules.addNotNullQuery('next_run_date');
		// if next run date is less than or equal to the end of today
		grSchedules.addQuery('next_run_date', '<=',gs.daysAgoEnd(0));
		grSchedules.query();
		while(grSchedules.next()){
			this.startRecurringTasker(grSchedules);

		}

	},

	// start any due to run today
	// update the last run date and log the date run into the recurringTasker run calendar
	startRecurringTasker: function(grScheduleToStart) {
		// Start processes due to run today
		var gdt = new GlideDateTime();
		var currentTime = gdt.getDisplayValue();			
		var recRunTemp = new GlideRecord('x_153465_discovert_discover_routing_runtime');
		recRunTemp.initialize();
		recRunTemp.setValue ( 'routing_template_ref', grScheduleToStart.tasker_process );
		gs.info("=========schedule tasker process: " +  grScheduleToStart.tasker_process );
				gs.info("=========schedule tasker process Objective: " +  grScheduleToStart.tasker_process.u_objective );

		// that is it, workflow is still going to fire off the insert of the Process Instance table
		// set the originator to the person who last updated the process schedule
		recRunTemp.originator = grScheduleToStart.sys_created_by;
	//	recRunTemp.originator = event.parm2;
		recRunTemp.instance_notation = currentTime;
		recRunTemp.u_objective = grScheduleToStart.tasker_process.u_objective;
		recRunTemp.insert();

		// update the last run on the Schedule table
		grScheduleToStart.last_run = new GlideDateTime();
		// below is to move back a day to test only!
		//grScheduleToStart.last_run = gs.daysAgoStart(1);
		grScheduleToStart.update();

		// update the schedule run history table
		var grScheduleHistory = new GlideRecord('x_153465_discovert_tasker_scheduler_history');
		grScheduleHistory.initialize();
		grScheduleHistory.run_note_from_schedule = grScheduleToStart.notes;
		// set this when you have the proc inst from the process you just started
		grScheduleHistory.tasker_process_instance = recRunTemp.sys_id;
		grScheduleHistory.tasker_process_template = grScheduleToStart.tasker_process;
		grScheduleHistory.tasker_schedule = grScheduleToStart.sys_id;
		grScheduleHistory.time_run = new GlideDateTime();
		grScheduleHistory.insert();

	},

	type: 'Tasker_RecurringTask_Functions'
};
