sys_script_include.script:
var dynamicSubCategoryFilter = Class.create();
dynamicSubCategoryFilter.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getsubCategories: function() {
		var subcategoryListArr = ' ';

		var grSubCategory = new GlideRecord('x_153465_discovert_discover_tasker_subcategory');
		//Only return sub categories with the given Category
		//gs.info('Discover Tasker - Dynamic Sub Category Filter - the list category selected is: ' + current.category.toString());
		grSubCategory.addQuery('category', current.category.toString());
		grSubCategory.addQuery('u_active', "true");
		//gs.info("dynamicSubCategory Query: "+grSubCategory.getEncodedQuery());
		grSubCategory.query();
		while(grSubCategory.next()){
			if (subcategoryListArr == ' '){
				subcategoryListArr += ( grSubCategory.sys_id);
			}
			else {
				subcategoryListArr += (',' + grSubCategory.sys_id);
			}
		}
		//gs.info('Discover Tasker - Dynamic Sub Category Filter - the list of subcategories returned is: ' + subcategoryListArr);
		return 'sys_id IN' + subcategoryListArr +'';
	},

	type: 'dynamicSubCategoryFilter'
});
