sys_script_include.script:
var TaskHelpers = Class.create();
TaskHelpers.prototype = {
	initialize: function() {
	},
    hasActiveTaskInOu: function(userId, ouId) {
		//lookup Task for assigned user in subject ou with active flag
		var records = new GlideAggregate("x_153465_discovert_route_task_instance");
		records.addQuery("assigned_to",userId);
		records.addQuery("primary_assignee", ouId);
		records.addQuery("active", true);
		records.addAggregate("COUNT");
		records.query();
		return records.getAggregate("COUNT") > 0;
	},

    type: 'TaskHelpers'
};
