var AdhocWorkflow = Class.create();
AdhocWorkflow.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	launchWorkflow: function(strWFID, current, operation, vars){		
		// RAJ - 2018-02-22 STRY0010778 - Remove Routes, everything in this script was only gs.info statements, the gs.info statements were changed to debug
		var wf = new global.Workflow();
		gs.debug("Discover_Process_Template - About to Launch - strWFID:" + strWFID + " - current_Sys_id: " + current.Sys_id + "- u_route_template_definition" + vars.u_route_template_definition + " - operation:" + operation);
		wf.startFlow(strWFID, current, operation, vars);
	},

    type: 'AdhocWorkflow'
});
