sys_script_include.script:
var TaskerNumberManager = Class.create();
TaskerNumberManager.prototype = {
    initialize: function() {
    },

    getNextTaskerNumberperPrimaryAssignee : function(pPrimaryAssignee) {

		var nextTaskerNumber = 1;

		var gaTaskerPerOUAssignee = new GlideAggregate('x_153465_discovert_route_task_instance');
		gaTaskerPerOUAssignee.addQuery('primary_assignee', pPrimaryAssignee); 
		gaTaskerPerOUAssignee.addEncodedQuery('numberperprimaryassigneeISNOTEMPTY');
		gaTaskerPerOUAssignee.addAggregate('COUNT');
		gaTaskerPerOUAssignee.query();

		if(gaTaskerPerOUAssignee.next()){
			nextTaskerNumber  = parseInt(gaTaskerPerOUAssignee.getAggregate('COUNT'))  + 1;		
		}

		return nextTaskerNumber;
	},

	padObjNumber : function(number, digits) {
    var num = number + "";
    while (num.length < digits)
       num = "0" + num;

    return num;
  },

	type: 'TaskerNumberManager'
};
