sys_script_include.script:
var TaskerTypeActions = Class.create();
TaskerTypeActions.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	IsActionAllowed: function(TaskerType,UIActionName)
	{
		//Query Tasker Type Actions to see if the UI Action Name belong to it. KTorres
		var grActionperTaskerType = new GlideAggregate('x_153465_discovert_tasker_type_actions');
		grActionperTaskerType.addQuery('tasker_type', TaskerType);
		grActionperTaskerType.addQuery('action', UIActionName);
		grActionperTaskerType.addAggregate('COUNT');
		grActionperTaskerType.query();

		var answer = false;
		if(grActionperTaskerType.next()){
			if (grActionperTaskerType.getAggregate('COUNT') > 0){
				answer = true;
			}
		}
		return answer;
	},

	type: 'TaskerTypeActions'
});
