sys_script_include.script:
var ClientTaskInfo = Class.create();
ClientTaskInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	taskParentHasSubtasks: function () {
		var parentID = this.getParameter("parentID");
		var data ;
		var gr = new GlideRecord('x_153465_discovert_route_task_instance');

		gr.addQuery('sys_id',parentID);
		gr.query();

		while(gr.next()){
			data = gr.calchaschildren;
		} 
		return data;
	},
	taskGetTaskSysId: function(){
		var suggested_task_number = this.getParameter("suggested_task_sys_id");
		var gr = new GlideRecord('x_153465_discovert_route_task_instance');

		gr.addQuery('suggested_task',suggested_task_number);
		gr.query();

		var new_task_id;

		while(gr.next()){
			new_task_id = gr.sys_id;
		} 
		return new_task_id;
	}
});
