sys_security_acl.script:
//check if this process template is one of user's OUs

var tcs = new TaskerCommonScriptingMethods();
try {
	var userInfoScripts = new UserInfoScripts();
	answer = current.process_level == "ou_process" && userInfoScripts.IsUserInOU(current.process_ou, gs.getUserID());
} catch(e) {
	answer = false;
	tcs.checkException("OU ACL", gs.warn, e);
}
