sys_ui_action.script:
// RAJ - STRY0011688 - A condition has been added to make sure this is not a new task and the task is still active
// TODO - who do we really want to be able to do this?  This should have a script that is run to determine the user meets the criteria we set and add it back to the condition
if(!current.primary_assignee.nil()){
	// Get the list of OUs that are direct children of the Primary Assignee OU
	var grOUs = new GlideRecord('x_153465_discovert_discover_office_hierarchy');
	grOUs.addQuery('parent', current.primary_assignee);
	//Only return members in the primary assignee office
	grOUs.query();
	// create a subtask with the same information as the parent for each member

	while(grOUs.next()){

		var recTaskInst = new GlideRecord('x_153465_discovert_route_task_instance');
		recTaskInst.initialize();
		recTaskInst.instructions = current.instructions;
		recTaskInst.category = current.category;
		recTaskInst.warning_hours = current.warning_hours;
		recTaskInst.classification = current.classification;

		// Set the Primary Assignee OU to the Child OU
		recTaskInst.primary_assignee = grOUs.sys_id;
		// NOTE: May need to add members to the Additional Assignee list
		//recTaskInst.assigned_to = grMem.office_member.toString();

		recTaskInst.primary_office = current.primary_office;
		recTaskInst.tasking_authority = current.tasking_authority;

		recTaskInst.short_description = current.short_description;
		recTaskInst.sub_category = current.sub_category;
		recTaskInst.parent = current.sys_id;
		recTaskInst.task_order = 1;
		// NOTE this should not be necessary, but the business rule that keeps these 2 in sync is not firing on this ui action
		recTaskInst.order = 1;

		// ***************** Start of changes	*****************
		// STRY0011008 - RAJ 2018-02-23 add logic to set dates while creating sub tasks
		//               the date logic used to be in a business rule, but that had negative impact on setting dates throughout a process tree
		var gdt = new GlideDateTime();
		var durationAdd;

		// Use parent to set Estimated Start, if child estimated start not set
		if(!current.expected_start.nil() && current.expected_start.nil()){
			recTaskInst.expected_start = current.expected_start;
			gdt.setValue(recTaskInst.expected_start);
		}
		else{
			recTaskInst.expected_start = gdt.getValue();
		}
		// NOTE: logicall calculating the duration below will overwrite the suspsense date.
		// if current record has no duration and no supsense date, and the parent has no suspense date, set it to the parent's duration
		if(!current.calendar_duration.nil()){
			recTaskInst.calendar_duration = current.calendar_duration;
			durationAdd=current.calendar_duration.dateNumericValue();
			gdt.add(durationAdd);
			recTaskInst.suspense_date=gdt.getValue();
			recTaskInst.due_date = current.suspense_date;
		}
		//       If use the parent values for suspense date instead of calculating based on duration
		if(!current.suspense_date.nil()) {
			recTaskInst.suspense_date = current.suspense_date;
			recTaskInst.due_date = current.suspense_date;
		}
		// ***************** End of changes	*****************
		recTaskInst.originator = gs.getUserID();
		recTaskInst.insert();

		// NOTE: we may want to only copy files from parent tasks if they are flagged somehow.
		// also we may want to copy the response documents from parent (marked only)  Or, the attachments object might be just fine.
		GlideSysAttachment.copy('x_153465_discovert_route_task_instance', current.sys_id, 'x_153465_discovert_route_task_instance', recTaskInst.sys_id);
	}
}
