sys_ui_action.script:
//https://community.servicenow.com/community?id=community_question&sys_id=b62643a1db1cdbc01dcaf3231f96199b
var taskerTable = 'x_153465_discovert_route_task_instance';
var grTasker = new GlideRecord(taskerTable);
var a_count = new GlideAggregate('sys_attachment');
a_count.addQuery('table_sys_id',current.sys_id);
a_count.addAggregate('COUNT');

a_count.query();
var numAttachments = 0;
if(a_count.next())
	numAttachments = a_count.getAggregate('COUNT');




grTasker.initialize();
grTasker.short_description = current.suggested_short_description;
grTasker.instructions = current.suggested_tasker_instructions;
grTasker.suggested_task = current.sys_id;




grTasker.work_notes = "This task was created from Suggested Task #: " + current.number.getDisplayValue()
+ "\n Sender: "+ current.sender_internal_user.name + "\n Sender Email: " + current.senderemail + "\n Subject Line: " + current.email_subject + "\n Date Emailed: " + current.sys_created_on + "\n Number of attachments: " + numAttachments;

// if(current.include_attachments){
// 	grTasker.work_notes ="Attachments were added from Suggested Task";
// }


grTasker.insert();

if(current.include_attachments){
	// RAJ - STRY0011879 - Dot walking reference field to sys_id
		GlideSysAttachment.copy('x_153465_discovert_suggested_taskers', current.sys_id, 'x_153465_discovert_route_task_instance', grTasker.sys_id);

}
current.u_created_task = grTasker.sys_id;

current.tasker_created = true;
current.active = false;

current.update();


var infoMsg ='<a href="../discovertaskerportal?id=discovertasker_taskdetails&table='+ taskerTable +'&sys_id=' + grTasker.sys_id+ '&spa=1" target="_top" >' + current.u_created_task.getDisplayValue() + ' has been created based on this suggestion' + '</a>. Please Wait. You are being redirected to the new Task.' ;
gs.addInfoMessage(infoMsg);

//Ayman - Redirect the user to the task page after task creation
var redirectUrl = "../discovertaskerportal?id=discovertasker_taskdetails&table=" + taskerTable + "&sys_id="+ grTasker.sys_id+ "&spa=1 target=_top";

//$location.url(redirectUrl);
action.setRedirectURL ( redirectUrl ) ;		



