sys_ui_action.script:
//previous condition:current.state == 14  && (current.tasker_type.getDisplayValue().toString() == 'Reverse Approval') 
promote();

function promote(){
	var grParentInstance = new GlideRecord('x_153465_discovert_route_task_instance');
	grParentInstance.initialize();

	// skip assignment, it will be handled by tasking authority
	//current.assigned_to = grSourceDefinition.assigned_to_individual;
	grParentInstance.instructions = current.instructions;
	grParentInstance.category = current.category;
	grParentInstance.warning_hours = current.warning_hours;
	grParentInstance.classification = current.classification;
	// TODO, FIND PARENT OU OF CURRENT TASK
	var grOU = new GlideRecord('x_153465_discovert_discover_office_hierarchy');
	grOU.addQuery('sys_id', current.primary_assignee);
	grOU.query();
	if(grOU.next()){
		grParentInstance.primary_assignee = grOU.parent;
	}

	grParentInstance.primary_office = current.primary_office;
	grParentInstance.task_order = 1;
	// TODO - don't do this with a single copy, need to determine the right way to do this
	//grParentInstance.response
	grParentInstance.short_description = current.short_description;
	grParentInstance.sub_category = current.sub_category;
	grParentInstance.calendar_duration = current.duration;
	grParentInstance.tasker_type = current.tasker_type;
	grParentInstance.tasking_authority = current.tasking_authority;
	grParentInstance.demo_data = current.demo_data;
	grParentInstance.originator = current.originator;
	grParentInstance.insert();
	current.parent = grParentInstance.sys_id;
	current.update();

	// NOTE: we may want to also copy the response documents from source to destination.  Or, the attachments object might be just fine.
	GlideSysAttachment.copy('x_153465_discovert_route_task_instance', current.sys_id, 'x_153465_discovert_route_task_instance', grParentInstance.sys_id);
	}
