sys_ui_action.script:
// RAJ changes for closing children
if(current.u_reviewer_signed == true)
	closeTask("Approved");
else
	gs.addErrorMessage('You must check the signed checkbox before submitting this task');

function closeTask(DecisionToClose){
	//var dialog = new GlideDialogWindow(""CloseChildrenCheck"");
	//dialog.setTitle(""Cancel All Children?"");
	//dialog.render();

	var blnClose = true;

	if  (current.calchaschildren > 0 &&
		current.progress_of_sub_tasks != 3 &&
	current.progress_of_sub_tasks != 4 &&
	current.progress_of_sub_tasks != 7){

		// TODO - this needs to become a dialog and push language somewhere to be read back
		//blnClose = confirm('TODO this Message needs to be changed into a dialog so users can make a choice and the text is read from a language table.             This Tasker has Sub Tasks that are not closed.  Do you want to ' + current.state + ' this Tasker and all sub taskers?  If yes, theclose note will be pushed to all open subtaskers.');
		blnClose = true;
	}
	if (blnClose){
		// NOTE: the updates happen on the server in a business rule.
		// So, in the client script, we just need to deal with the user cancelling the update
		// reset the assigned members to the previous value on cancel

		current.decision = DecisionToClose;

		current.state = 3;
		current.active = 'false';
		var gdt = new GlideDateTime();
		current.work_end = gdt.getValue();
		current.work_notes = "This task has been signed by " + current.assigned_to.name;

		current.update();
		// TODO: TO MOVE IT TO POPUP IN MULTILANGUAGE. KTORRES
		gs.addInfoMessage(current.number + ' has been successfully '+ DecisionToClose +' and marked as Completed');
	}

}
