sys_ui_policy.script_false:
function onCondition() {	
	// active always false
	g_form.setVisible("active", false);
	g_form.setSectionDisplay("instructions", false);
	g_form.setVisible("u_signature_instructions", false);
	g_form.setMandatory("u_signature_instructions", false);
	g_form.setReadOnly("u_signature_instructions", true);
}

sys_ui_policy.script_true:
function onCondition() {
	var type = g_form.getValue("tasker_type");
// 		jslog("Signature Instructions - Firing!");
		g_form.setSectionDisplay("instructions", true);
		g_form.setVisible("u_signature_instructions", true);

		//get originator, reviewer and active
		var reviewer = g_form.getValue("u_reviewer");
		var originator = g_form.getValue("originator");
		var isActive = g_form.getValue("active");

		if(isActive == "true" && (g_user && g_user.userID == reviewer || g_user.userID == originator || originator == "")) {
// 			jslog("Signature Instructions - active: " + isActive + " user: " + g_user.userID + " reviewer: " + reviewer + " originator: " + originator);
			g_form.setReadOnly("u_signature_instructions", false);
			g_form.setMandatory("u_signature_instructions", true);
		}	
}
