sys_ui_policy.script_false:
function onCondition() {
	var numChildren = g_form.getValue("parent.calchaschildren");
	if(numChildren <= 1) {
		g_form.setDisplay("route_order", false);
	} else {
		g_form.setDisplay("route_order", true);
	}
	g_form.setDisplay("parent.calchaschildren", false);
}

sys_ui_policy.script_true:
function onCondition() {
	var numChildren = g_form.getValue("parent.calchaschildren");
	if(numChildren <= 1) {
		g_form.setDisplay("route_order", false);
	} else {
		g_form.setDisplay("route_order", true);
	}
	g_form.setDisplay("parent.calchaschildren", false);	
}
